svg.script:
const eff1 = document.querySelector('.line1');
const tL = eff1.getTotalLength();
const keyframe = [
  {
    strokeDasharray: tL + "px",
    strokeDashoffset: tL + "px"
  },
  {
    strokeDasharray: tL + "px",
    strokeDashoffset: "0px"
  }
];
const dur = {
  duration: 1500,
  iterations: Infinity,
   fill: "backwards",
};
eff1.animate(keyframe, dur);
